import { OrbitingCircles } from "../../components/ui/OrbitingCirclesProps";
import iklogo from '@/assets/logo4.png'
import Image from "next/image";
import { useEffect, useState } from "react";

export function OrbitingCirclesDemo() {
  const [iconSize, setIconSize] = useState(30);
  const [radius, setRadius] = useState(120);

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 640) { // Small devices
        setIconSize(25);
        setRadius(140);
      } else if (window.innerWidth < 768) { // Medium devices
        setIconSize(35);
        setRadius(160);
      } else { // Large devices
        setIconSize(45);
        setRadius(200);
      }
    };

    // Set initial values
    handleResize();

    // Add event listener
    window.addEventListener('resize', handleResize);
    
    // Cleanup event listener on component unmount
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div className="relative lg:ml-20 flex h-[500px] w-full flex-col items-center justify-center overflow-hidden rounded-lg">
      {/* IK Logo */}
      <span className="pointer-events-none whitespace-pre-wrap bg-gradient-to-b from-black to-gray-300 bg-clip-text text-center text-6xl sm:text-8xl font-semibold leading-none text-transparent dark:from-white dark:to-black">
        <Image 
          src={iklogo} 
          alt="IK Logo" 
          layout="intrinsic"
          width={80} // Default size for small devices
          height={80} // Default size for small devices
          className="rounded-full sm:w-[120px] sm:h-[120px]" // Larger size for larger devices
        />
      </span>

      {/* Orbiting Circles 1 */}
      <OrbitingCircles iconSize={iconSize} radius={radius} className="lg:-mt-1.5 lg:mr-8 sm:mt-8 sm:ml-8">
        <Icons.whatsapp />
        <Icons.notion />
        <Icons.grapana />
        <Icons.googleDrive />
        <Icons.jenkins />
        <Icons.reactjs />
      </OrbitingCircles>

      {/* Orbiting Circles 2 */}
      <OrbitingCircles iconSize={iconSize - 5} radius={radius - 60} reverse speed={1} className="lg:-mt-1.5 lg:mr-8 sm:mt- mt-0 sm:ml-8">
        <Icons.pytorch />
        <Icons.new4j />
        <Icons.gitLab />
        <Icons.gitActions />
      </OrbitingCircles>
    </div>
  );
}


